        Console.WriteLine("Зима");
        break;
    case 3:
    case 4:
    case 5:
        Console.WriteLine("Весна");
        break;
    default:
        Console.WriteLine("Некорректный месяц");
        break;
}

int dayNumber = 1;
string dayName = dayNumber switch
{
    1 => "Понедельник",
    2 => "Вторник",
    3 => "Среда",
    _ => "Неизвестный день"
};
Console.WriteLine(dayName);

