{
    Console.WriteLine("Значение в допустимом диапазоне");
}
else
{
    Console.WriteLine("Значение вне диапазона");
}

bool hasPermission = true;
bool isLoggedIn = false;

if (hasPermission && isLoggedIn)
{
    Console.WriteLine("Доступ разрешен");
}
else if (!isLoggedIn)
{
    Console.WriteLine("Необходимо войти в систему");
}

double num1 = 10;
double num2 = 3;
char operation = '/';
double result;

switch (operation)
{
    case '+': result = num1 + num2; break;
    case '-': result = num1 - num2; break;
    case '*': result = num1 * num2; break;
    case '/':
        result = num2 != 0 ? num1 / num2 : 0;
        break;
    default: result = 0; break;
}
Console.WriteLine($"Результат: {result:F2}");

int age = 16;
string category = age < 13 ? "Ребенок" : age < 20 ? "Подросток" : age < 60 ? "Взрослый" : "Пенсионер";
Console.WriteLine($"Возрастная категория: {category}");

int hour = 14;
string greeting = hour >= 5 && hour < 12 ? "Доброе утро!" : hour >= 12 && hour < 17 ? "Добрый день!" : hour >= 17 && hour < 22 ? "Добрый вечер!" : "Доброй ночи!";
Console.WriteLine(greeting);

char symbol = 'A';
if (char.IsLetter(symbol))
{
    Console.WriteLine(char.IsUpper(symbol) ? "Символ - заглавная буква" : "Символ - строчная буква");
}
else if (char.IsDigit(symbol))
{
    Console.WriteLine("Символ - цифра");
}

int CheckNumber(int number)
{
    if (number < 0) return -1;
    if (number == 0) return 0;
    return 1;
}
int result = CheckNumber(-5);
Console.WriteLine($"Результат: {result}");

enum Status { Active, Inactive, Pending }
Status userStatus = Status.Pending;
string message = userStatus switch
{
    Status.Active => "Пользователь активен",
    Status.Inactive => "Пользователь неактивен",
    Status.Pending => "Ожидает подтверждения",
    _ => "Неизвестный статус"
};
Console.WriteLine(message);

string password = "MyPass123";
bool isValid = password.Length >= 8 && password.Any(char.IsDigit) && password.Any(char.IsUpper);
Console.WriteLine(isValid ? "Пароль корректный!" : "Пароль не соответствует требованиям");

int a = 5, b = 2, c = 8;
int max = a >= b && a >= c ? a : b >= c ? b : c;
int min = a <= b && a <= c ? a : b <= c ? b : c;
int mid = a + b + c - max - min;
Console.WriteLine($"По убыванию: {max}, {mid}, {min}");

int year = 2024;
bool isLeapYear = year % 400 == 0 || (year % 4 == 0 && year % 100 != 0);
Console.WriteLine(isLeapYear ? $"{year} - високосный год" : $"{year} - обычный год");

int x = 3, y = -2;
string quadrant = (x, y) switch
{
    (> 0, > 0) => "I квадрант",
    (< 0, > 0) => "II квадрант",
    (< 0, < 0) => "III квадрант",
    (> 0, < 0) => "IV квадрант",
    (0, _) => "На оси X",
    (_, 0) => "На оси Y"
};
Console.WriteLine($"Точка ({x}, {y}) находится в: {quadrant}");

int a = 3, b = 4, c = 5;
bool isTriangle = a + b > c && a + c > b && b + c > a;
if (isTriangle)
{
    if (a == b && b == c) Console.WriteLine("Равносторонний");
    else if (a == b || b == c || a == c) Console.WriteLine("Равнобедренный");
    else Console.WriteLine("Обычный");
}
else Console.WriteLine("Не треугольник");

int mainChoice = 2, subChoice = 1;
switch (mainChoice)
{
    case 1:
        Console.WriteLine("Настройки");
        break;
    case 2:
        Console.WriteLine("Графика");
        Console.WriteLine(subChoice == 1 ? " - Разрешение" : subChoice == 2 ? " - Качество" : " - Неизвестная опция");
        break;
    default:
        Console.WriteLine("Неизвестный раздел");
        break;
}

string userName = null;
if (string.IsNullOrEmpty(userName))
{
    Console.WriteLine("Пользователь не указал имя");
}
else
{
    Console.WriteLine($"Привет, {userName}!");
}

int number = 25;
string category = number switch
{
    0 => "Ноль",
    > 0 and <= 10 => "Малое число",
    > 10 and <= 50 => "Среднее число",
    > 50 and <= 100 => "Большое число",
    < 0 => "Отрицательное",
    _ => "Очень большое"
};
Console.WriteLine($"Число {number}: {category}");

string input = "25";
bool isValid = !string.IsNullOrWhiteSpace(input) && int.TryParse(input, out int number) && number >= 0 && number <= 100;

if (isValid)
{
    string result = (number % 2 == 0 ? "Четное" : "Нечетное") + " " + (number > 50 ? "большое" : "маленькое");
    Console.WriteLine($"Число {number}: {result}");
}
else
{
    Console.WriteLine("Ошибка ввода!");
}
